mapper.select:
SELECT uc.CHR_ID as chrId, uc.BILL_NO as billNo, uc.BILLTYPE_ID as billtypeId, UC.EN_ID as enId, EIE.CHR_CODE as enCode, uc.PAYERID as payerid, uc.PAYER as payerName, eu.ACCOUNT_NAME as enAccountNo, uc.REMARK as remark, uc.CREATE_USER as createUser, UC.PM_ID as pmId, UC.MAKEDATE as makedate,uc.RECEIVETYPE as receivetype, CASE WHEN uc.RECEIVETYPE = 1 THEN( SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTIONDETAIL WHERE main_id = uc.CHR_ID) WHEN uc.RECEIVETYPE = 2 THEN( SELECT SUM (CHARGEMONEY) FROM UNTAX_EDU_COLLECTION_DETAIL WHERE STU_MAINID = uc.CHR_CODE4) ELSE(SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTION_GATHER_DETAIL WHERE main_id IN (SELECT CHR_ID FROM UNTAX_COLLECTION_GATHER WHERE COLLECT_ID = uc.CHR_ID)) END chargemoney, uc.SET_YEAR as setYear, vur.CHR_NAME as receName,vur.ACCOUNT_NO as receAccountNo,vur.BANK_NAME as receBankName, vur.ISBN as isbn, uc.RECEIVERID as receiverid, uc.PAYERACCOUNT as payeraccount, uc.PAYERBANK as payerbank, uc.CONSIGNEE_EN_ID as consigneeEnId,uc.REQ_BILL_NO as reqBillNo, uc.ERASEFLAG as eraseflag, uc.PAYFLAG as payflag, uc.CHECK_FLAG as checkFlag,vur.BANK_NO as bankNo,uc.NOSOURCE_IDS as nosourceIds,uc.PRINTFLAG as printflag, CASE WHEN uc.BILL_NAME is null then ub.CHR_NAME ELSE uc.BILL_NAME END billName, CASE WHEN uc.EN_NAME is null then EIE.CHR_NAME ELSE uc.EN_NAME END enName, CASE WHEN uc.PM_NAME is null then eup.CHR_NAME ELSE uc.PM_NAME END pmName, CASE WHEN uc.USER_NAME is null then SU.USER_NAME ELSE uc.USER_NAME END userName, CASE WHEN uc.CONSIGNEE_EN_NAME is null then EIE1.CHR_NAME ELSE uc.CONSIGNEE_EN_NAME END consigneeEnName, eie1.CHR_CODE as consigneeEnCode, uc.CHR_CODE1 as chrCode1,uc.CHR_CODE2 as chrCode2,uc.CHR_CODE3 as chrCode3,uc.CHR_CODE4 as chrCode4 FROM UNTAX_COLLECTION uc,UNTAX_BILLNAME ub,ELE_INCOME_ENTERPRISE eie,ELE_INCOME_ENTERPRISE eie1,ELE_UNTAXACCOUNT eu,SYS_USERMANAGE su, ELE_UNTAXPAYMODE eup, VW_UNTAX_RECEIVERACCOUNT vur WHERE uc.IS_DELETED=0 and uc.BILLTYPE_ID = ub.CHR_ID and UC.EN_ID = EIE.CHR_ID and uc.PAYERID = eu.CHR_ID(+) and uc.CREATE_USER = su.USER_ID and UC.PM_ID = eup.CHR_ID and vur.CHR_ID = uc.RECEIVERID and UC.CONSIGNEE_EN_ID = EIE1.CHR_ID(+)
AND uc.CHR_ID=#{chrId}
AND uc.REQ_BILL_NO=#{reqBillNo}
AND uc.CHR_CODE4=#{chrCode4}

mapper.update:
UPDATE
	UNTAX_COLLECTION
SET
	PAYFLAG=#{payflag},PAYDATE=#{paydate}


		AND REQ_BILL_NO=#{reqBillNo}


		AND CHR_ID=#{chrId}

UPDATE
	UNTAX_COLLECTION
SET
	LATEFLAG=#{lateflag},LATE_DATE=#{latedate}


		AND REQ_BILL_NO=#{reqBillNo}


		AND CHR_ID=#{chrId}

UPDATE
	UNTAX_COLLECTION
SET
	IS_SEND=#{isSend}
WHERE
	REQ_BILL_NO=#{reqBillNo}

mapper.select:
SELECT
	count(uc.CHR_ID) as num
FROM
	UNTAX_COLLECTION uc, SYS_USERMANAGE su, ELE_UNTAXACCOUNT eu
WHERE
	uc.IS_DELETED = '0'

	AND uc.ISCONSIGN = #{isconsign}

	AND uc.CREATE_USER = su.USER_ID and uc.PAYERID = eu.CHR_ID(+)
	AND uc.RECEIVETYPE = #{receivetype}

	AND uc.CREATE_USER=#{createUser}


	AND uc.PAYER like CONCAT(CONCAT('%',#{payerName}),'%')


	AND uc.ERASEFLAG=0


	AND uc.PRINTFLAG=0


	AND uc.PRINTFLAG=1 AND uc.PAYFLAG=0 AND uc.ERASEFLAG=0


	AND uc.PRINTFLAG=0


	AND uc.PRINTFLAG=1 AND uc.PAYFLAG=0 AND uc.CHECKEARNE=0 AND uc.ERASEFLAG=0


	AND uc.PRINTFLAG=1 AND uc.PAYFLAG=1 AND uc.CHECKEARNE=0 AND uc.ERASEFLAG=0


	AND uc.PRINTFLAG=1 AND uc.PAYFLAG=1 AND uc.CHECKEARNE=0 AND uc.ERASEFLAG=0


	AND uc.PRINTFLAG=1 AND uc.PAYFLAG=1 AND uc.CHECKEARNE=1 AND uc.ERASEFLAG=0


	AND uc.ERASEFLAG=1


	AND uc.EN_ID=#{enId}



		AND uc.CONSIGNEE_EN_ID IN

			#{incomeEnterprise.enId}




	AND uc.MAKEDATE >= #{beginMakedate}


	AND uc.MAKEDATE <= #{endMakedate}


	AND uc.PM_ID = #{pmId}


	AND uc.BILLTYPE_ID = #{billtypeId}


	AND uc.BILL_NO >= #{beginBillNo}


	AND uc.BILL_NO <= #{endBillNo}


	AND uc.chr_id in(select MAIN_ID from UNTAX_COLLECTIONDETAIL where INCITEMID=#{incitemid})

SELECT * FROM ( SELECT ROWNUM AS rowno, ub.* FROM ( select uc.CHR_ID as chrId, uc.BILL_NO as billNo, uc.BILLTYPE_ID as billtypeId,UC.EN_ID as enId, uc.PAYERID as payerid, uc.PAYER as payerName, uc.REMARK as remark, uc.CREATE_USER as createUser, UC.PM_ID as pmId,UC.MAKEDATE as makedate, CASE WHEN uc.RECEIVETYPE = 1 THEN( SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTIONDETAIL WHERE main_id = uc.CHR_ID) ELSE(SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTION_GATHER_DETAIL WHERE main_id IN (SELECT CHR_ID FROM UNTAX_COLLECTION_GATHER WHERE COLLECT_ID = uc.CHR_ID)) END chargemoney, vur.ACCOUNT_NO as receiveraccount,vur.BANK_NAME as receiverbank, vur.CHR_NAME as receiver, CASE WHEN uc.BILL_NAME is null then ub.CHR_NAME ELSE uc.BILL_NAME END billName, CASE WHEN uc.EN_NAME is null then EIE.CHR_NAME ELSE uc.EN_NAME END enName, CASE WHEN uc.PM_NAME is null then eup.CHR_NAME ELSE uc.PM_NAME END pmName, CASE WHEN uc.USER_NAME is null then SU.USER_NAME ELSE uc.USER_NAME END userName, CASE WHEN uc.CONSIGNEE_EN_NAME is null then eie1.CHR_NAME ELSE uc.CONSIGNEE_EN_NAME END consigneeEnName from UNTAX_COLLECTION uc,UNTAX_BILLNAME ub,ELE_INCOME_ENTERPRISE eie,ELE_INCOME_ENTERPRISE eie1,ELE_UNTAXACCOUNT eu,SYS_USERMANAGE su, ELE_UNTAXPAYMODE eup, VW_UNTAX_RECEIVERACCOUNT vur WHERE uc.IS_DELETED=0 and uc.BILLTYPE_ID = ub.CHR_ID and UC.EN_ID = EIE.CHR_ID and UC.CONSIGNEE_EN_ID = eie1.CHR_ID(+) and uc.PAYERID = eu.CHR_ID(+) and uc.CREATE_USER = su.USER_ID and UC.PM_ID = eup.CHR_ID
AND uc.ISCONSIGN = #{collections.isconsign}
AND uc.RECEIVETYPE = #{collections.receivetype} AND vur.CHR_ID = uc.RECEIVERID
AND uc.CREATE_USER=#{collections.createUser}
AND uc.PAYER like CONCAT(CONCAT('%',#{collections.payerName}),'%')
AND uc.ERASEFLAG=0
AND uc.PRINTFLAG=0
AND uc.PRINTFLAG=1 AND uc.PAYFLAG=0 AND uc.ERASEFLAG=0
AND uc.PRINTFLAG=0
AND uc.PRINTFLAG=1 AND uc.PAYFLAG=0 AND uc.CHECKEARNE=0 AND uc.ERASEFLAG=0
AND uc.PRINTFLAG=1 AND uc.PAYFLAG=1 AND uc.CHECKEARNE=0 AND uc.ERASEFLAG=0
AND uc.PRINTFLAG=1 AND uc.PAYFLAG=1 AND uc.CHECKEARNE=0 AND uc.ERASEFLAG=0
AND uc.PRINTFLAG=1 AND uc.PAYFLAG=1 AND uc.CHECKEARNE=1 AND uc.ERASEFLAG=0
AND uc.ERASEFLAG=1
AND uc.EN_ID=#{collections.enId}
AND uc.CONSIGNEE_EN_ID IN
#{incomeEnterprise.enId}
AND uc.MAKEDATE >= #{collections.beginMakedate}
AND uc.MAKEDATE <= #{collections.endMakedate}
AND uc.PM_ID = #{collections.pmId}
AND uc.BILLTYPE_ID = #{collections.billtypeId}
AND uc.BILL_NO >= #{collections.beginBillNo}
AND uc.BILL_NO <= #{collections.endBillNo}
AND uc.chr_id in(select MAIN_ID from UNTAX_COLLECTIONDETAIL where INCITEMID=#{collections.incitemid})
ORDER BY ${sort} ${order}
ORDER BY uc.CREATE_DATE desc
) ub where ROWNUM <= #{endrow} ) ub WHERE ub.rowno > #{startrow}

SELECT uc.CHR_ID as chrId, uc.BILL_NO as billNo, uc.BILLTYPE_ID as billtypeId, ub.CHR_NAME as billName,UC.EN_ID as enId, EIE.CHR_CODE as enCode, EIE.CHR_NAME as enName, uc.PAYERID as payerid, uc.PAYER as payerName, uc.PAYERACCOUNT as payeraccount, uc.PAYERBANK as payerbank, uc.REMARK as remark, uc.CREATE_USER as createUser, SU.USER_NAME as userName, UC.PM_ID as pmId,eup.CHR_NAME as pmName, UC.MAKEDATE as makedate, CASE WHEN uc.RECEIVETYPE = 1 THEN( SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTIONDETAIL WHERE main_id = uc.CHR_ID) ELSE(SELECT SUM (CHARGEMONEY) FROM UNTAX_EDU_COLLECTION_DETAIL WHERE stu_mainid IN (SELECT CHR_ID FROM UNTAX_EDU_COLLECTION WHERE CHR_ID = uc.CHR_CODE4)) END allmoney, UC.RECEIVERID as receiverid,vur.ACCOUNT_NAME as receiver,vur.ACCOUNT_NO as receiveraccount,vur.BANK_NAME as receiverbank,uc.RECEIVETYPE as receivetype, uc.CHR_CODE1 as chrCode1,uc.CHR_CODE2 as chrCode2,uc.CHR_CODE3 as chrCode3,uc.CHR_CODE4 as chrCode4 FROM UNTAX_COLLECTION uc,UNTAX_BILLNAME ub,ELE_INCOME_ENTERPRISE eie,ELE_UNTAXACCOUNT eu,SYS_USERMANAGE su, ELE_UNTAXPAYMODE eup,VW_UNTAX_RECEIVERACCOUNT vur WHERE uc.IS_DELETED=0 and uc.BILLTYPE_ID = ub.CHR_ID and UC.EN_ID = EIE.CHR_ID and uc.PAYERID = eu.CHR_ID(+) and uc.CREATE_USER = su.USER_ID and UC.PM_ID = eup.CHR_ID and UC.RECEIVERID = vur.CHR_ID
and uc.CHR_ID=#{chrId}
AND uc.CHR_CODE4=#{chrCode4}

SELECT * FROM ( SELECT ROWNUM AS rowno, ub.* FROM ( select uc.CHECKEARNE as checkearne, uc.CHR_ID as chrId, uc.BILL_NO as billNo, uc.BILLTYPE_ID as billtypeId,uc.EN_ID as enId, uc.PAYERID as payerid, uc.PAYER as payerName, uc.REMARK as remark, uc.CREATE_USER as createUser, UC.PM_ID as pmId,uc.MAKEDATE as makedate,uc.RECEIVETYPE as receivetype,uc.CHECKDATE as checkdate, CASE WHEN uc.RECEIVETYPE = 1 THEN( SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTIONDETAIL WHERE main_id = uc.CHR_ID) WHEN uc.RECEIVETYPE = 2 THEN( SELECT SUM (CHARGEMONEY) FROM UNTAX_EDU_COLLECTION_DETAIL WHERE STU_MAINID = uc.CHR_CODE4) ELSE(SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTION_GATHER_DETAIL WHERE main_id IN (SELECT CHR_ID FROM UNTAX_COLLECTION_GATHER WHERE COLLECT_ID = uc.CHR_ID)) END chargemoney, vur.ACCOUNT_NO as receiveraccount,vur.BANK_NAME as receiverbank, vur.CHR_NAME as receiver, CASE WHEN uc.BILL_NAME is null then ub.CHR_NAME ELSE uc.BILL_NAME END billName, CASE WHEN uc.ISCONSIGN = 1 THEN ( CASE WHEN uc.CONSIGNEE_EN_NAME is null then EIE1.CHR_NAME ELSE uc.CONSIGNEE_EN_NAME END ) ELSE ( CASE WHEN uc.EN_NAME is null then EIE.CHR_NAME ELSE uc.EN_NAME END ) END enName, CASE WHEN uc.PM_NAME is null then eup.CHR_NAME ELSE uc.PM_NAME END pmName, CASE WHEN uc.USER_NAME is null then SU.USER_NAME ELSE uc.USER_NAME END userName, uc.NOSOURCE_IDS as nosourceIds from UNTAX_COLLECTION uc,UNTAX_BILLNAME ub,ELE_INCOME_ENTERPRISE eie,ELE_INCOME_ENTERPRISE eie1,ELE_UNTAXACCOUNT eu,SYS_USERMANAGE su, ELE_UNTAXPAYMODE eup, VW_UNTAX_RECEIVERACCOUNT vur WHERE uc.IS_DELETED=0 and uc.BILLTYPE_ID = ub.CHR_ID and UC.EN_ID = EIE.CHR_ID and UC.CONSIGNEE_EN_ID = EIE1.CHR_ID(+) and uc.PAYERID = eu.CHR_ID(+) and uc.CREATE_USER = su.USER_ID and UC.PM_ID = eup.CHR_ID AND vur.CHR_ID = uc.RECEIVERID and uc.ERASEFLAG=0 AND uc.CHECK_FLAG = 1
AND uc.RECEIVERID=#{collections.receiverid}
AND uc.PAYER like CONCAT(CONCAT('%',#{collections.payerName}),'%')
AND uc.CHECKEARNE = 0
AND uc.CHECKEARNE = 1
AND uc.PAYFLAG=0
AND uc.PAYFLAG=1
AND uc.EN_ID=#{collections.enId}
AND uc.CHECKDATE >= #{collections.beginCheckdate}
AND uc.CHECKDATE <= #{collections.endCheckdate}
AND uc.MAKEDATE >= #{collections.beginMakedate}
AND uc.MAKEDATE <= #{collections.endMakedate}
AND uc.PM_ID = #{collections.pmId}
AND uc.BILLTYPE_ID = #{collections.billtypeId}
AND uc.BILL_NO >= #{collections.beginBillNo}
AND uc.BILL_NO <= #{collections.endBillNo}
AND uc.chr_id in(select MAIN_ID from UNTAX_COLLECTIONDETAIL where INCITEMID=#{collections.incitemid})
AND uc.NOSOURCE_IDS is null
AND uc.NOSOURCE_IDS is not null
ORDER BY ${sort} ${order}
ORDER BY uc.CREATE_DATE desc
) ub where ROWNUM <= #{endrow} ) ub WHERE ub.rowno > #{startrow}

SELECT
	count(uc.CHR_ID) as num
FROM
	UNTAX_COLLECTION uc, SYS_USERMANAGE su, ELE_UNTAXACCOUNT eu
WHERE
	uc.IS_DELETED = '0' AND uc.CREATE_USER = su.USER_ID and uc.PAYERID = eu.CHR_ID(+)  and uc.ERASEFLAG=0 AND uc.CHECK_FLAG = 1

	AND RECEIVERID=#{receiverid}


	AND uc.PAYER like CONCAT(CONCAT('%',#{payerName}),'%')


	AND uc.CHECKEARNE = 0


	AND uc.CHECKEARNE=1


	AND uc.PAYFLAG=0


	AND uc.PAYFLAG=1


	AND uc.EN_ID=#{enId}


	AND uc.MAKEDATE >= #{beginMakedate}


	AND uc.MAKEDATE <= #{endMakedate}


	AND uc.CHECKDATE >= #{beginCheckdate}


	AND uc.CHECKDATE <= #{endCheckdate}


	AND uc.PM_ID = #{pmId}


	AND uc.BILLTYPE_ID = #{billtypeId}


	AND uc.BILL_NO >= #{beginBillNo}


	AND uc.BILL_NO <= #{endBillNo}


	AND uc.chr_id in(select MAIN_ID from UNTAX_COLLECTIONDETAIL where INCITEMID=#{incitemid})



		AND uc.NOSOURCE_IDS is null


		AND uc.NOSOURCE_IDS is not null

SELECT * FROM ( SELECT ROWNUM AS rowno, ub.* FROM ( select uc.CHECKEARNE as checkearne, uc.CHR_ID as chrId, uc.BILL_NO as billNo, uc.BILLTYPE_ID as billtypeId,uc.EN_ID as enId, uc.PAYERID as payerid, uc.PAYER as payerName, uc.REMARK as remark, uc.CREATE_USER as createUser, UC.PM_ID as pmId,uc.MAKEDATE as makedate,uc.RECEIVETYPE as receivetype, CASE WHEN uc.RECEIVETYPE = 1 THEN( SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTIONDETAIL WHERE main_id = uc.CHR_ID) ELSE(SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTION_GATHER_DETAIL WHERE main_id IN (SELECT CHR_ID FROM UNTAX_COLLECTION_GATHER WHERE COLLECT_ID = uc.CHR_ID)) END chargemoney, vur.ACCOUNT_NO as receiveraccount,vur.BANK_NAME as receiverbank, vur.CHR_NAME as receiver, CASE WHEN uc.BILL_NAME is null then ub.CHR_NAME ELSE uc.BILL_NAME END billName, CASE WHEN uc.EN_NAME is null then EIE.CHR_NAME ELSE uc.EN_NAME END enName, CASE WHEN uc.PM_NAME is null then eup.CHR_NAME ELSE uc.PM_NAME END pmName, CASE WHEN uc.USER_NAME is null then SU.USER_NAME ELSE uc.USER_NAME END userName, uc.PAYFLAG as payflag from UNTAX_COLLECTION uc,UNTAX_BILLNAME ub,ELE_INCOME_ENTERPRISE eie,ELE_UNTAXACCOUNT eu,SYS_USERMANAGE su, ELE_UNTAXPAYMODE eup, VW_UNTAX_RECEIVERACCOUNT vur WHERE uc.IS_DELETED=0 and uc.BILLTYPE_ID = ub.CHR_ID and UC.EN_ID = EIE.CHR_ID and uc.PAYERID = eu.CHR_ID(+) and uc.CREATE_USER = su.USER_ID and UC.PM_ID = eup.CHR_ID AND vur.CHR_ID = uc.RECEIVERID and uc.ERASEFLAG=0 and uc.PRINTFLAG=1 AND uc.RECEIVERID IN
#{incomeBankAccount.chrId}
AND uc.PAYFLAG=0
AND uc.PAYFLAG=1
AND uc.BILL_NO >= #{collections.beginBillNo}
AND uc.BILL_NO <= #{collections.endBillNo}
AND uc.BILLTYPE_ID = #{collections.billtypeId}
AND (CASE WHEN uc.RECEIVETYPE = 1 THEN( SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTIONDETAIL WHERE main_id = uc.CHR_ID) ELSE(SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTION_GATHER_DETAIL WHERE main_id IN (SELECT CHR_ID FROM UNTAX_COLLECTION_GATHER WHERE COLLECT_ID = uc.CHR_ID)) END) = #{collections.chargemoneyStr}
ORDER BY ${sort} ${order}
ORDER BY uc.CREATE_DATE desc
) ub where ROWNUM <= #{endrow} ) ub WHERE ub.rowno > #{startrow}

SELECT
    count(uc.CHR_ID) as num
FROM
	UNTAX_COLLECTION uc, SYS_USERMANAGE su, ELE_UNTAXACCOUNT eu
WHERE
	uc.IS_DELETED = '0' AND uc.CREATE_USER = su.USER_ID AND uc.PAYERID = eu.CHR_ID(+) AND uc.ERASEFLAG=0 AND uc.PRINTFLAG=1
	AND uc.RECEIVERID IN

		#{incomeBankAccount.chrId}


		AND uc.PAYFLAG=0


		AND uc.PAYFLAG=1


		AND uc.BILL_NO >= #{beginBillNo}


		AND uc.BILL_NO <= #{endBillNo}


		AND uc.BILLTYPE_ID = #{billtypeId}


		AND (CASE WHEN uc.RECEIVETYPE = 1 THEN( SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTIONDETAIL WHERE main_id = uc.CHR_ID)
		ELSE(SELECT SUM (CHARGEMONEY) FROM UNTAX_COLLECTION_GATHER_DETAIL WHERE main_id IN (SELECT CHR_ID FROM UNTAX_COLLECTION_GATHER WHERE COLLECT_ID = uc.CHR_ID))
		END) = #{chargemoneyStr}
