mapper.select:
SELECT SET_YEAR,CHR_ID,CHR_CODE,DISP_CODE,CHR_NAME,LEVEL_NUM, IS_LEAF,ENABLED,CREATE_DATE,CREATE_USER,LATEST_OP_DATE, IS_DELETED,LATEST_OP_USER,LAST_VER,CHR_CODE1,CHR_CODE2, CHR_CODE3,CHR_CODE4,CHR_CODE5,CHR_ID1,CHR_ID2,CHR_ID3, CHR_ID4,CHR_ID5,PARENT_ID,RG_CODE FROM ELE_MANAGE_BRANCH WHERE IS_DELETED=0
AND CHR_ID=#{chrId}
AND CHR_CODE=#{chrCode}

SELECT count(CHR_ID) as num FROM ELE_MANAGE_BRANCH WHERE IS_DELETED = '0'
AND SET_YEAR = #{setYear}
start with CHR_ID = #{chrId} connect by prior CHR_ID = PARENT_ID

		SELECT 
			*
		 FROM (
			SELECT ROWNUM AS rowno, ebk.* FROM (
				SELECT
					ebk.CHR_CODE as chrCode,ebk.DISP_CODE as dispCode,ebk.CHR_NAME as chrName,ebk.LEVEL_NUM as levelNum,ebk.ENABLED as enabled,ebk.IS_DELETED as isDeleted,
					ebk.IS_LEAF as isLeaf, ebk.CHR_ID as chrId, nvl(ebk1.CHR_NAME, '全部') as parentName, nvl(ebk.PARENT_ID, 'allTree') as parentId
				FROM
					ELE_MANAGE_BRANCH ebk,ELE_MANAGE_BRANCH ebk1
    			WHERE
					ebk.IS_DELETED = '0' and ebk.PARENT_ID = ebk1.CHR_ID(+)

                        AND ebk.SET_YEAR = #{manageBranch.setYear}


						start with ebk.CHR_ID = #{manageBranch.chrId} connect by prior ebk.CHR_ID = ebk.PARENT_ID



						ORDER BY ${sort} ${order}


						ORDER BY ebk.DISP_CODE ASC


		  ) ebk where  ROWNUM <= #{endrow} 
		) ebk
		WHERE  ebk.rowno > #{startrow}

SELECT ebk.CHR_ID as chrId, ebk.CHR_CODE as chrCode,ebk.DISP_CODE as dispCode,ebk.CHR_NAME as chrName,ebk.LEVEL_NUM as levelNum,ebk.ENABLED as enabled,ebk.IS_DELETED as isDeleted, ebk.IS_LEAF as isLeaf, ebk.PARENT_ID as parentId, ebk.PARENT_NAME as parentName FROM ELE_MANAGE_BRANCH ebk WHERE ebk.IS_DELETED = '0'
AND ebk.SET_YEAR = #{setYear}
AND ebk.CHR_ID=#{chrId}
AND ebk.PARENT_ID=#{parentId}
ORDER BY ebk.DISP_CODE ASC
